<template>
    <div class="collection-card">
        <div class="img-wrap" @click="toCollectionPage">
            <img src="data:image/jpeg;base64,"
                alt="Обложка коллекции">
        </div>
        <div class="collection-info">
            <p>{{ collection.name }}</p>
            <p>{{ collection.description }}</p>
            <div class="collection-additional-info">
                <button>
                    <i class="bi bi-hand-thumbs-up"></i>
                    {{ collection.likes }}
                </button>
                <p>
                    <i class="bi bi-calendar-date"></i>
                    {{ collection.date }}
                </p>
            </div>
        </div>
    </div>
</template>
<script setup lang="ts">
import { Collection } from '@/common/contracts';
import { defineProps, PropType, ref } from 'vue';
import { useRouter } from 'vue-router';

const router = useRouter();

const props = defineProps({
    collection: {
        type: Object as PropType<Collection>,
        required: true
    }
});

const toCollectionPage = () => {
    router.push({ name: 'collection', params: { collectionId: props.collection.id.toString() } });
}

</script>

